using Xunit;
using Xunit.Abstractions;

namespace Aoc2015.Tests.Day1
{
    public class Day1aTests : TestBase
    {
        public Day1aTests(ITestOutputHelper output) : base(output) { }

        [Theory]
        [InlineData("(())", 0)]
        [InlineData("()()", 0)]
        [InlineData("(((", 3)]
        [InlineData("(()(()(", 3)]
        [InlineData("))(((((", 3)]
        [InlineData("())", -1)]
        [InlineData("))(", -1)]
        [InlineData(")))", -3)]
        [InlineData(")())())", -3)]
        public void Spec(string input, int expectedOutput)
        {
            int actual = Day1a.Calculate(input);
            Assert.Equal(expectedOutput, actual);
        }

        [Fact]
        public void A()
        {
            int result = Day1a.Calculate(TestData.InputA);
            WriteLine(result.ToString());
        }
    }

    public class Day1bTests : TestBase
    {
        public Day1bTests(ITestOutputHelper output) : base(output) { }

        [Theory]
        [InlineData(")", 1)]
        [InlineData("()())", 5)]
        public void Spec(string input, int expected)
        {
            Assert.Equal(expected, Day1b.Calculate(input));
        }


        [Fact]
        public void CalculateDay1b()
        {
            WriteLine(Day1b.Calculate(TestData.InputA).ToString());
        }
    }

